var crc32=require(".."),test=require("tap").test;test("simple crc32 is no problem",function(e){var t=Buffer("hey sup bros"),n=Buffer([71,250,85,112]);e.same(crc32(t),n),e.end()}),test("another simple one",function(e){var t=Buffer("IEND"),n=Buffer([174,66,96,130]);e.same(crc32(t),n),e.end()}),test("slightly more complex",function(e){var t=Buffer([0,0,0]),n=Buffer([255,65,217,18]);e.same(crc32(t),n),e.end()}),test("complex crc32 gets calculated like a champ",function(e){var t=Buffer("शीर्षक"),n=Buffer([23,184,175,241]);e.same(crc32(t),n),e.end()}),test("casts to buffer if necessary",function(e){var t="शीर्षक",n=Buffer([23,184,175,241]);e.same(crc32(t),n),e.end()}),test("can do signed",function(e){var t="ham sandwich",n=-1891873021;e.same(crc32.signed(t),n),e.end()}),test("can do unsigned",function(e){var t="bear sandwich",n=3711466352;e.same(crc32.unsigned(t),n),e.end()}),test("simple crc32 in append mode",function(e){var t=[Buffer("hey"),Buffer(" "),Buffer("sup"),Buffer(" "),Buffer("bros")],n=Buffer([71,250,85,112]);for(var r=0,i=0;i<t.length;i++)r=crc32(t[i],r);e.same(r,n),e.end()}),test("can do signed in append mode",function(e){var t="ham",n=" ",r="sandwich",i=-1891873021,s=crc32.signed(t);s=crc32.signed(n,s),s=crc32.signed(r,s),e.same(s,i),e.end()}),test("can do unsigned in append mode",function(e){var t="bear san",n="dwich",r=3711466352,i=crc32.unsigned(t);i=crc32.unsigned(n,i),e.same(i,r),e.end()});