function convert(e){var t=1<<30,n=1<<20,r=1024;return e>=t?Math.round(e/t*100)/100+"gb":e>=n?Math.round(e/n*100)/100+"mb":e>=r?Math.round(e/r*100)/100+"kb":e+"b"}module.exports=function(e){if("number"==typeof e)return convert(e);var t=e.match(/^(\d+(?:\.\d+)?) *(kb|mb|gb)$/),n=parseFloat(t[1]),r=t[2],i={kb:1024,mb:1<<20,gb:1<<30};return i[r]*n};