var crypto=require("crypto");exports.sign=function(e,t){if("string"!=typeof e)throw new TypeError("cookie required");if("string"!=typeof t)throw new TypeError("secret required");return e+"."+crypto.createHmac("sha256",t).update(e).digest("base64").replace(/\=+$/,"")},exports.unsign=function(e,t){if("string"!=typeof e)throw new TypeError("cookie required");if("string"!=typeof t)throw new TypeError("secret required");var n=e.slice(0,e.lastIndexOf("."));return exports.sign(n,t)==e?n:!1};