var readdirp=require(".."),path=require("path"),es=require("event-stream");readdirp({root:path.join(__dirname),fileFilter:"*.js"}).on("warn",function(e){console.error("non-fatal error",e)}).on("error",function(e){console.error("fatal error",e)}).pipe(es.mapSync(function(e){return{path:e.path,size:e.stat.size}})).pipe(es.stringify()).pipe(process.stdout);