var assert=require("assert"),wordwrap=require("wordwrap"),fs=require("fs"),idleness=fs.readFileSync(__dirname+"/idleness.txt","utf8");exports.stop80=function(){var e=wordwrap(80)(idleness).split(/\n/),t=idleness.split(/\s+/);e.forEach(function(e){assert.ok(e.length<=80,"line > 80 columns");var n=e.match(/\S/)?e.split(/\s+/):[];assert.deepEqual(n,t.splice(0,n.length))})},exports.start20stop60=function(){var e=wordwrap(20,100)(idleness).split(/\n/),t=idleness.split(/\s+/);e.forEach(function(e){assert.ok(e.length<=100,"line > 100 columns");var n=e.split(/\s+/).filter(function(e){return e.match(/\S/)});assert.deepEqual(n,t.splice(0,n.length)),assert.deepEqual(e.slice(0,20),(new Array(21)).join(" "))})};