function cleanup(){Person.remove(function(){mongoose.disconnect()})}var async=require("async"),mongoose=require("../../lib");require("./person.js")();var Person=mongoose.model("Person"),data=[{name:"bill",age:25,birthday:(new Date).setFullYear((new Date).getFullYear()-25),gender:"Male",likes:["movies","games","dogs"]},{name:"mary",age:30,birthday:(new Date).setFullYear((new Date).getFullYear()-30),gender:"Female",likes:["movies","birds","cats"]},{name:"bob",age:21,birthday:(new Date).setFullYear((new Date).getFullYear()-21),gender:"Male",likes:["tv","games","rabbits"]},{name:"lilly",age:26,birthday:(new Date).setFullYear((new Date).getFullYear()-26),gender:"Female",likes:["books","cats","dogs"]},{name:"alucard",age:1e3,birthday:(new Date).setFullYear((new Date).getFullYear()-1e3),gender:"Male",likes:["glasses","wine","the night"]}];mongoose.connect("mongodb://localhost/persons",function(e){if(e)throw e;async.each(data,function(e,t){Person.create(e,t)},function(e){Person.aggregate({$project:{name:1,likes:1}},{$unwind:"$likes"},{$group:{_id:{likes:"$likes"},likers:{$addToSet:"$name"}}},function(e,t){if(e)throw e;console.log(t),cleanup()})})});