function cleanup(){Person.remove(function(){mongoose.disconnect()})}var mongoose=require("../../lib");require("./person.js")();var Person=mongoose.model("Person");mongoose.connect("mongodb://localhost/persons",function(e){if(e)throw e;Person.create({name:"bill",age:25,birthday:(new Date).setFullYear((new Date).getFullYear()-25)},function(e,t){if(e)throw e;console.log("People added to db: %s",t.toString()),Person.find({},function(e,t){if(e)throw e;t.forEach(function(e){console.log("People in the db: %s",e.toString())}),setTimeout(function(){cleanup()},2e3)})})});