function cleanup(){Person.remove(function(){mongoose.disconnect()})}var async=require("async"),mongoose=require("../../lib");require("./person.js")();var Person=mongoose.model("Person"),data=[{name:"bill",age:25,birthday:(new Date).setFullYear((new Date).getFullYear()-25)},{name:"mary",age:30,birthday:(new Date).setFullYear((new Date).getFullYear()-30)},{name:"bob",age:21,birthday:(new Date).setFullYear((new Date).getFullYear()-21)},{name:"lilly",age:26,birthday:(new Date).setFullYear((new Date).getFullYear()-26)},{name:"alucard",age:1e3,birthday:(new Date).setFullYear((new Date).getFullYear()-1e3)}],opts={replSet:{rs_name:"rs0"}};mongoose.connect("mongodb://localhost:27018/persons,localhost:27019,localhost:27020",opts,function(e){if(e)throw e;async.each(data,function(e,t){Person.create(e,t)},function(e){var t=Person.find({age:{$lt:1e3}}).exec();t.then(function(e){console.log("young people: %s",e)}).then(cleanup)})});