function cleanup(){Person.remove(function(){mongoose.disconnect()})}var mongoose=require("../../lib");require("./person.js")();var Person=mongoose.model("Person");mongoose.connect("mongodb://localhost/persons",function(e){if(e)throw e;Person.create({name:"bill",age:25,birthday:(new Date).setFullYear((new Date).getFullYear()-25)},function(e,t){if(e)throw e;console.log("People added to db: %s",t.toString()),Person.findPersonByName("bill",function(e,t){if(e)throw e;console.log(t),cleanup()})})});