WebInspector.TextUtils={isStopChar:function(e){return e>" "&&e<"0"||e>"9"&&e<"A"||e>"Z"&&e<"_"||e>"_"&&e<"a"||e>"z"&&e<="~"},isWordChar:function(e){return!WebInspector.TextUtils.isStopChar(e)&&!WebInspector.TextUtils.isSpaceChar(e)},isSpaceChar:function(e){return WebInspector.TextUtils._SpaceCharRegex.test(e)},isWord:function(e){for(var t=0;t<e.length;++t)if(!WebInspector.TextUtils.isWordChar(e.charAt(t)))return!1;return!0},isOpeningBraceChar:function(e){return e==="("||e==="{"},isClosingBraceChar:function(e){return e===")"||e==="}"},isBraceChar:function(e){return WebInspector.TextUtils.isOpeningBraceChar(e)||WebInspector.TextUtils.isClosingBraceChar(e)},textToWords:function(e){var t=[],n=-1;for(var r=0;r<e.length;++r)WebInspector.TextUtils.isWordChar(e.charAt(r))?n===-1&&(n=r):(n!==-1&&t.push(e.substring(n,r)),n=-1);return n!==-1&&t.push(e.substring(n)),t}},WebInspector.TextUtils._SpaceCharRegex=/\s/,WebInspector.TextUtils.Indent={TwoSpaces:"  ",FourSpaces:"    ",EightSpaces:"        ",TabCharacter:"	"};