var redis=require("redis"),client=redis.createClient();redis.RedisClient.prototype.parse_info=function(e){this.info(function(t,n){var r=n.toString().split("\r\n").sort(),i={};r.forEach(function(e){var t=e.split(":");t[1]&&(i[t[0]]=t[1])}),e(i)})},client.parse_info(function(e){console.dir(e),client.quit()});