var WebSocketServer=require("../../").Server,http=require("http"),express=require("express"),app=express();app.use(express.static(__dirname+"/public"));var server=http.createServer(app);server.listen(8080);var wss=new WebSocketServer({server:server});wss.on("connection",function(e){var t=setInterval(function(){e.send(JSON.stringify(process.memoryUsage()),function(){})},100);console.log("started client interval"),e.on("close",function(){console.log("stopping client interval"),clearInterval(t)})});