var WebSocketServer=require("../../").Server,http=require("http"),express=require("express"),app=express.createServer();app.use(express.static(__dirname+"/public")),app.listen(8080);var wss=new WebSocketServer({server:app});wss.on("connection",function(e){var t=setInterval(function(){e.send(JSON.stringify(process.memoryUsage()),function(){})},100);console.log("started client interval"),e.on("close",function(){console.log("stopping client interval"),clearInterval(t)})});