var Sender=require("../lib/Sender");require("should"),describe("Sender",function(){describe("#frameAndSend",function(){it("does not modify a masked binary buffer",function(){var e=new Sender({write:function(){}}),t=new Buffer([1,2,3,4,5]);e.frameAndSend(2,t,!0,!0),t[0].should.eql(1),t[1].should.eql(2),t[2].should.eql(3),t[3].should.eql(4),t[4].should.eql(5)}),it("does not modify a masked text buffer",function(){var e=new Sender({write:function(){}}),t="hi there";e.frameAndSend(1,t,!0,!0),t.should.eql("hi there")})})});