function decode(e){if(!/^data:/.test(e))throw new Error('Not a data URI: "'+truncate(e,20)+'"');var t=e.indexOf(",");if(t<0)throw new Error('Invalid data URI "'+truncate(e,20)+'"');var n=e.slice(0,t),r=e.slice(t+1);return/;base64$/.test(n)?new Buffer(r,"base64"):new Buffer(decodeURIComponent(r),"ascii")}var truncate=require("truncate");exports.decode=decode;